    public static IxTheoAnnotation stringToIxTheoAnnotation(String s) {
        return IxTheoAnnotation.valueOf(s);
    }

    private final String description;
    private final String shortDescription;

    private IxTheoAnnotation(String description) {
        this.description = description;
        this.shortDescription = this.description.split(" ")[0];
    }

    public String toShortString() {
        return shortDescription;
    }

    @Override
    public String toString() {
        return description;
    }

    public boolean equals(IxTheoAnnotation obj) {
        if ((obj == null) || (obj.getClass() != this.getClass())) {
            return false;
        } else if (!obj.toShortString().equals(this.toShortString())) {
            return false;
        } else
            return true;

    }
